public interface Coordinates {
  double getLatitude();
  double getLongitude();
}

import org.hibernate.search.spatial.Coordinates;
import org.hibernate.search.annotations.*;

@Entity
@Indexed
@Spatial
public class SimpleHotel implements Coordinates {
  [..]

org.apache.lucene.search.Query luceneQuery = SpatialQueryBuilder.buildSimpleSpatialQuery(
		centerLatitude, centerLongitude, radius);

org.hibernate.Query hibQuery = fullTextSession.createFullTextQuery( luceneQuery,
	SimpleHotel.class );
List results = hibQuery.list();

@Entity
@Indexed
@Spatial(name = "location", spatialMode = SpatialMode.GRID)
public class Hotel implements Coordinates {
	double latitude;
	double longitude;
  [...]

org.apache.lucene.search.Query luceneQuery =
	SpatialQueryBuilder.buildSpatialQuery(
		centerLatitude,
		centerLongitude,
		50,
		"location"
	);
org.hibernate.Query hibQuery = fullTextSession.createFullTextQuery( luceneQuery,
	Hotel.class );
List results = hibQuery.list();
